import React, { useState } from 'react';
import { View, Text, FlatList, TextInput, TouchableOpacity } from 'react-native';

import libraries from './data/libraries'; // Mocked data

const LibraryListScreen = ({ navigation }) => {
  const [searchText, setSearchText] = useState('');
  const filteredLibraries = libraries.filter(library =>
    library.name.toLowerCase().includes(searchText.toLowerCase())
  );

  return (
    <View>
      <TextInput
        placeholder="Search libraries"
        value={searchText}
        onChangeText={setSearchText}
      />
      <FlatList
        data={filteredLibraries}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <TouchableOpacity onPress={() => navigation.navigate('LibraryDetail', { library: item })}>
            <View>
              <Text>{item.name}</Text>
              <Text>{item.summary}</Text>
            </View>
          </TouchableOpacity>
        )}
      />
    </View>
  );
};

export default LibraryListScreen;
