import React, { useState } from 'react';
import { View, Text, FlatList, TextInput, TouchableOpacity, Image, StyleSheet, Button } from 'react-native';

import libraries from './data/libraries'; // Mocked data

const LibraryListScreen = ({ navigation }) => {
  const [searchText, setSearchText] = useState('');
  const filteredLibraries = libraries.filter((library) =>
    library.name.toLowerCase().includes(searchText.toLowerCase())
  );

  const [sortedLibraries, setSortedLibraries] = useState(libraries);

  // Function to sort libraries by popularity
  const sortLibrariesByPopularity = (libraries) => {
    return libraries.slice().sort((a, b) => b.popularity - a.popularity);
  };

  const sortByPopularity = () => {
    const sorted = sortLibrariesByPopularity(sortedLibraries);
    setSortedLibraries(sorted);
  };

  return (
    <View style={styles.container}>
      <Button title="Sort by Popularity" onPress={sortByPopularity} />
      <TextInput
        placeholder="Search libraries"
        value={searchText}
        onChangeText={setSearchText}
        style={styles.input}
      />
      <FlatList
        data={filteredLibraries}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <TouchableOpacity
            style={styles.card}
            onPress={() => navigation.navigate('LibraryDetail', { library: item })}
          >
            <Image source={{ uri: item.imageUri }} style={styles.image} />
            <View style={styles.cardContent}>
              <Text style={styles.title}>{item.name}</Text>
              <Text>{item.summary}</Text>
            </View>
          </TouchableOpacity>
        )}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
  },
  input: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    padding: 8,
  },
  card: {
    backgroundColor: '#fff',
    borderRadius: 10,
    padding: 10,
    marginBottom: 10,
    flexDirection: 'row',
  },
  image: {
    width: 80,
    height: 80,
    borderRadius: 5,
    marginRight: 10,
  },
  cardContent: {
    flex: 1,
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
  },
});

export default LibraryListScreen;
