import React, { useState } from 'react';
import { View, Text, Image, Button } from 'react-native';

const LibraryDetailScreen = ({ route }) => {
  const { library } = route.params;
  const [isBookmarked, setIsBookmarked] = useState(false);

  const handleBookmark = () => {
    setIsBookmarked(!isBookmarked);
  };

  // Use dynamic import to load images
  const loadImage = (imageUri) => {
    return {
      uri: imageUri,
    };
  };

  return (
    <View>
      <Image source={loadImage(library.imageUri)} style={{ width: 200, height: 200 }} />
      <Text>{library.name}</Text>
      <Text>{library.address}</Text>
      <Text>{library.operationalHours}</Text>
      <Text>{library.history}</Text>
      <Text>{library.services}</Text>
      <Button title={isBookmarked ? 'Unbookmark' : 'Bookmark'} onPress={handleBookmark} />
    </View>
  );
};

export default LibraryDetailScreen;
